import React, { useEffect, useRef } from 'react';
import anime from 'animejs';
import './shapes.css';

// Circle Animation from anime.js library
const CircleAnimation = () => {
  useEffect(() => {
    anime({ // call the anime.js library
      targets: '.circle',
	  translateY: 90,
	  rotate: 405,
      scale: 5, // Scale the circle to twice its size
      opacity: 0.6, // Change the opacity to 0.5
      duration: 2000, // Animation duration in milliseconds
      easing: 'linear', // Easing function for the animation
    });
  }, []); 

  return <div className="circle"></div>;
};

// Line Animation from anime.js library
export const LineAnimation = function({x1, y1, x2, y2}) {
	useEffect(() => {
	  anime({ // call the anime.js library
		targets: '.line',
		strokeDashoffset: [anime.setDashoffset, 0], // Animates the stroke dash offset from its initial value to 0
		strokeDasharray: [anime.setDashoffset, 0], // Animates the stroke dash array from its initial value to 0
		duration: 2000,
		easing: 'easeInOutQuad',
	  });
	}, []);

	return (
	  <svg width="100%" height="100%" xmlns="http://www.w3.org/2000/svg">
		<line className="line" x1={x1} y1={y1} x2={x2} y2={y2} stroke="black" strokeWidth="1.5" />
	  </svg>
	);
};

// test shape function for creating more animations
// this function is probably going to be used for the naviagation bar
// figure out how to make this function work for the other components
// Ideas for animations: make an animation for 
const shapeFunction = function({x1}) {
	useEffect(() => {
		anime({
			targets: '.line',
			strokeDashoffset: [anime.setDashoffset, 0],
			strokeDashoffset: [anime.setDashoffset, 0],
			duration: 1000,
			easing: 'easeOutSine'
		});
		anime({
			targets: '.line',
			strokeDasharray: [anime.setDashoffset, 0],
			strokeDashoffset: [anime.setDashoffset, 0],
			duration: 500,
			easing: 'easeoutSine',
		});
	});

	return <div className="circle"></div>;
};


export default CircleAnimation;
