import React, { useState, useEffect } from 'react';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import { NavbarSmall }  from './navigation';
import { ReactTyped } from 'react-typed';
import { Button } from '@mui/material';
import { FontContext, FontProvider } from './fontContext';
import './styles/header.css'; // Import the CSS file


const ChangeFontButton = ({onClick}) => {
  return (
    <Button onClick={onClick} variant='text' color='inherit'>
      F
    </Button>
  )
}

function Header({ onFontChange }) {

  // Font indexing for changing font family
  const [fontIndex, setFontIndex] = useState(FontContext);
  function changeFont() { // prevIndex = previous state (index) 
    setFontIndex((prevIndex) => (prevIndex + 1) % fontFamilies.length);
  }

  useEffect(() => {
    document.body.style.fontFamily = fontFamily;
  }, [fontFamily]);

  const fontFamily = fontFamilies[fontIndex];

  return (
    <div>
    <AppBar position="fixed" className="app_header" elevation={0}
		  sx={{backgroundColor: "var(--primary-color-dark)"}}>
      <Box display="flex" justifyContent="center">
        <Toolbar sx={{width: '70vw', justifyContent: 'space-between'}}>
          <Typography component="a" href="/" className='app_header_name' 
            sx={{fontFamily: fontFamily, color: 'var(--primary-color-light2)', textDecoration: 'none', fontSize: '1.5em'}}>
              nero.me
              <ReactTyped 
                strings={[' ']} 
                cursorChar='_'
                />
          </Typography>
          <ChangeFontButton onClick={changeFont}/>
          <NavbarSmall className="app_header_nav"/>
        </Toolbar>
      </Box>
    </AppBar>
    </div>
  );
}

export default Header;
