import React, { useEffect, useRef } from 'react';
import anime from 'animejs';
import './shapes.css';

const CircleAnimation = () => {
  useEffect(() => {
    anime({
      targets: '.circle',
	  translateY: 120,
	  rotate: 360,
      scale: 6, // Scale the circle to twice its size
      opacity: 0.6, // Change the opacity to 0.5
      duration: 1000, // Animation duration in milliseconds
      easing: 'easeInOutQuad', // Easing function for the animation
    });
  }, []);

  return <div className="circle"></div>;
};

export const LineAnimation = () => {
	useEffect(() => {
	  anime({
		targets: '.line',
		strokeDashoffset: [anime.setDashoffset, 0], // Animates the stroke dash offset from its initial value to 0
		strokeDasharray: [anime.setDashoffset, 0], // Animates the stroke dash array from its initial value to 0
		duration: 2000,
		easing: 'easeInOutQuad',
	  });
	}, []);

	return (
	  <svg width="300" height="200" xmlns="http://www.w3.org/2000/svg">
		<line className="line" x1="50" y1="100" x2="250" y2="100" stroke="black" strokeWidth="1" />
	  </svg>
	);
  };

export default CircleAnimation;
