import React from 'react';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import { NavbarSmall }  from './navigation';
import CustomCursor from './cursor/cursor';
import { ReactTyped } from 'react-typed';
import { Button } from '@mui/material';
import './styles/header.css'; // Import the CSS file

const fontFamilies = [
  'var(--font-family-text)',
  'var(--font-family-gt)',
  'var(--font-family-ibm-plex-mono)',
  'var(--font-family-geist)'
]



function Header() {

  const [fontIndex, setFontIndex] = useState(0);
  const changeFont = () => { // prevIndex = previous state (index) 
    setFontIndex((prevIndex) => (prevIndex + 1) % fontFamilies.length);
  }

  useEffect(() => {
    document.body.style.fontFamily = fontFamilies[fontIndex];
  }, [fontIndex]);

  const fontFamily = getComputedStyle(document.documentElement).getPropertyValue('--font-family-ibm-plex-mono');

  return (
    <div>
    <AppBar position="fixed" className="app_header" elevation={0}
		  sx={{backgroundColor: "var(--primary-color-dark)"}}>
      <Box display="flex" justifyContent="center">
        <Toolbar sx={{width: '70vw', justifyContent: 'space-between'}}>
          <Typography component="a" href="/" className='app_header_name' 
            sx={{fontFamily: fontFamily, color: 'var(--primary-color-light2)', textDecoration: 'none', fontSize: '1.5em'}}>
              nero.me
              <ReactTyped 
                strings={[' ']} 
                cursorChar='_'
                />
          </Typography>
          <NavbarSmall className="app_header_nav"/>
        </Toolbar>
      </Box>
    </AppBar>
    </div>
  );
}

export default Header;
